body.script:
function getQueryVariable(variable) {
    const query = window.location.search.substring(1);
    const vars = query.split("&");
    for (let i = 0; i < vars.length; i++) {
        const pair = vars[i].split("=");
        if (pair[0] === variable) {
            return pair[1];
        }
    }
    return false;
}

function loadCV(lang) {
    const xhttp = new XMLHttpRequest();
    xhttp.onload = function() {
        if (this.status == 200) {
            document.getElementById("cv-content").innerHTML = this.responseText;
        } else {
            document.getElementById("cv-content").innerHTML = "Błąd ładowania CV.";
        }
    }
    xhttp.open("GET", "cv_" + lang + ".xml", true);
    xhttp.send();
}

const lang = getQueryVariable("lang") || "pol";
document.documentElement.lang = lang;
loadCV(lang);
